import React from 'react'
import FeedbackItem from './FeedbackItem';
import  PropTypes from 'prop-types';
import {motion, AnimatePresence} from 'framer-motion';


const FeedbackLists = ({feedback, handleDelete}) => {

    if(!feedback || feedback.length === 0){
        return <p className='nolists'>No FeedBack Yet</p>
    }

    //  Animation----->

    return (

      <div className='feedback-list'>
              <AnimatePresence>

              {feedback.map((item) =>(
              <motion.div key={item.id} initial={{opacity:0}} animate={{opacity:1}} exit={{opacity:0.4}} >
               <FeedbackItem key={item.id} item={item} handleDelete = {handleDelete} />
             </motion.div>
            ))}
                  </AnimatePresence>

      </div>

    );

    // without Animation----->

  // return (
  //   <div className='feedback-list'>
  //           {feedback.map((item) =>(
  //        <FeedbackItem key={item.id}
  //         item={item}
  //         handleDelete = {handleDelete} />
  //         ))}
  //   </div>
  // );

}

FeedbackLists.propTypes = {
  feedback : PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.number.isRequired,
      rating: PropTypes.number.isRequired,
      text: PropTypes.string.isRequired,
    })
  )
}

export default FeedbackLists;
